javascript:void(function(){var gerarCoord=function(coord,bb){var ret=coord.substr(0,3)+'|'+coord.substr(-3);if(bb){ret='[coord]'+ret+'[/coord]';}
return ret;};var aldeias={'bonusb':[],'bonusp':[],'barb':[]};var config={'barb_maiorq':0,'bonus_maiorq':parseInt(prompt('Pontuação mínima de aldeia bonus',1000)),'mostrar_barb':confirm('Considerar barbaras sem bônus?'),'codigo_bb':confirm('Aldeias em código BB?')};if(config.mostrar_barb){config.barb_maiorq=parseInt(prompt('Pontuação mínima de aldeia barbara:',1000));}
for(var coord in TWMap.villages){var villa=TWMap.villages[coord];if(villa.bonus&&parseInt(villa.points.replace(/\./g,''))>=config.bonus_maiorq){if(villa.owner=='0'){aldeias.bonusb.push(gerarCoord(coord,config.codigo_bb));}else{if(TWMap.allyRelations[TWMap.players[villa.owner].ally]!='partner'){aldeias.bonusp.push(gerarCoord(coord,config.codigo_bb));}}}else if(config.mostrar_barb&&parseInt(villa.points.replace(/\./g,''))>=config.barb_maiorq){aldeias.barb.push(gerarCoord(coord,config.codigo_bb));}}
prompt('Aldeias bônus barbara',aldeias.bonusb.join(' '));prompt('Aldeias bônus de inimigos',aldeias.bonusp.join(' '));if(config.mostrar_barb){prompt('Aldeias barbara',aldeias.barb.join(' '));}})();